import { DeserializeHtml } from '@udecode/plate-core';

export const deserializeHtmlBold: DeserializeHtml[] = [
  { validNodeName: ['STRONG', 'B'] },
  {
    validStyle: {
      fontWeight: ['600', '700', 'bold'],
    },
  },
];
import {
  createPluginFactory,
  onKeyDownToggleMark,
  ToggleMarkPlugin,
} from '@udecode/plate-core';
import { deserializeHtmlBold } from './deserializeHtmlBold';

export const MARK_BOLD = 'bold';

/**
 * Enables support for bold formatting
 */
export const createBoldPlugin = createPluginFactory<ToggleMarkPlugin>({
  key: MARK_BOLD,
  isLeaf: true,
  deserializeHtml: deserializeHtmlBold,
  handlers: {
    onKeyDown: onKeyDownToggleMark,
  },
  options: {
    hotkey: 'mod+b',
  },
});
import {
  createPluginFactory,
  findHtmlParentElement,
  onKeyDownToggleMark,
  ToggleMarkPlugin,
} from '@udecode/plate-core';

export const MARK_CODE = 'code';

/**
 * Enables support for code formatting
 */
export const createCodePlugin = createPluginFactory<ToggleMarkPlugin>({
  key: MARK_CODE,
  isLeaf: true,
  deserializeHtml: [
    {
      validNodeName: ['CODE'],
      query: (el) => !findHtmlParentElement(el, 'PRE'),
    },
    {
      validStyle: {
        wordWrap: 'break-word',
      },
    },
    {
      validStyle: {
        fontFamily: 'Consolas',
      },
    },
  ],
  handlers: {
    onKeyDown: onKeyDownToggleMark,
  },
  options: {
    hotkey: 'mod+e',
  },
});
export const deserializeHtmlItalic = [
  { validNodeName: ['EM', 'I'] },
  {
    validStyle: {
      fontStyle: 'italic',
    },
  },
];
import {
  createPluginFactory,
  onKeyDownToggleMark,
  ToggleMarkPlugin,
} from '@udecode/plate-core';
import { deserializeHtmlItalic } from './deserializeHtmlItalic';

export const MARK_ITALIC = 'italic';

/**
 * Enables support for italic formatting.
 */
export const createItalicPlugin = createPluginFactory<ToggleMarkPlugin>({
  key: MARK_ITALIC,
  isLeaf: true,
  handlers: {
    onKeyDown: onKeyDownToggleMark,
  },
  options: {
    hotkey: 'mod+i',
  },
  deserializeHtml: deserializeHtmlItalic,
});
import {
  createPluginFactory,
  onKeyDownToggleMark,
  ToggleMarkPlugin,
} from '@udecode/plate-core';

export const MARK_STRIKETHROUGH = 'strikethrough';

/**
 * Enables support for strikethrough formatting.
 */
export const createStrikethroughPlugin = createPluginFactory<ToggleMarkPlugin>({
  key: MARK_STRIKETHROUGH,
  isLeaf: true,
  handlers: {
    onKeyDown: onKeyDownToggleMark,
  },
  options: {
    hotkey: 'mod+shift+x',
  },
  deserializeHtml: [
    { validNodeName: ['S', 'DEL', 'STRIKE'] },
    {
      validStyle: {
        textDecoration: 'line-through',
      },
    },
  ],
});
import {
  createPluginFactory,
  onKeyDownToggleMark,
  ToggleMarkPlugin,
} from '@udecode/plate-core';

export const MARK_SUBSCRIPT = 'subscript';
const MARK_SUPERSCRIPT = 'superscript';

/**
 * Enables support for subscript formatting.
 */
export const createSubscriptPlugin = createPluginFactory<ToggleMarkPlugin>({
  key: MARK_SUBSCRIPT,
  isLeaf: true,
  handlers: {
    onKeyDown: onKeyDownToggleMark,
  },
  options: {
    hotkey: 'mod+,',
    clear: MARK_SUPERSCRIPT,
  },
  deserializeHtml: [
    { validNodeName: ['SUB'] },
    {
      validStyle: {
        verticalAlign: 'sub',
      },
    },
  ],
});
import {
  createPluginFactory,
  onKeyDownToggleMark,
  ToggleMarkPlugin,
} from '@udecode/plate-core';

export const MARK_SUPERSCRIPT = 'superscript';
const MARK_SUBSCRIPT = 'subscript';

/**
